 <template>
  <div class="produtos-page">
    <h1>Nossos Acessórios</h1>
     <p>Na nossa loja, os produtos são como </p>
    <div class="grid">
      <div class="card" v-for="produto in produtos" :key="produto.id">
        <img :src="produto.img" alt="produto" />

        <h3>{{ produto.nome }}</h3>
        <p>R$ {{ produto.preco.toFixed(2) }}</p>

        <button @click="adicionar(produto)">
          Adicionar ao carrinho
        </button>
        <p v-if="msg" class="msg">{{ msg }}</p>
      </div>
    </div>
  </div>
</template>

<script setup>
import { ref } from "vue"

const produtos = ref([
  {
    id: 1,
    nome: "Brinco dourado",
    preco: 29.9,
    img: "data:image/jpeg;base64,"
  },
  {
    id: 2,
    nome: "Anel elegante",
    preco: 39.9,
    img: "data:image/jpeg;base64,"
  },
  {
    id: 3,
    nome: "Colar fino",
    preco: 49.9,
    img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTsy0UyPP9bnrW5rDwSwDfkz3vqVp_-WDo8iQ&s"
  },
  {
    id: 4,
    nome: "Pulseira moderna",
    preco: 24.9,
    img: "data:image/jpeg;base64,"
  }
])

function adicionar(produto) {
  const carrinho = JSON.parse(localStorage.getItem("carrinho")) || []
  carrinho.push(produto)
  localStorage.setItem("carrinho", JSON.stringify(carrinho))

}
</script>

